mapper.insert:
insert into sys_permission (id, code, name, 
  permission_module_id, url, type, 
  status, seq, remark, 
  operator, operate_time, operate_ip
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{permissionModuleId,jdbcType=INTEGER}, #{url,jdbcType=VARCHAR}, #{type,jdbcType=INTEGER}, 
  #{status,jdbcType=INTEGER}, #{seq,jdbcType=INTEGER}, #{remark,jdbcType=VARCHAR}, 
  #{operator,jdbcType=VARCHAR}, #{operateTime,jdbcType=TIMESTAMP}, #{operateIp,jdbcType=VARCHAR}
  )

mapper.select:
SELECT count(1)
FROM sys_permission
WHERE name = #{name}

  AND permission_module_id = #{permissionModuleId}


  AND id != #{id}

SELECT count(1)
FROM sys_permission
WHERE permission_module_id = #{permissionModuleId}

SELECT 
FROM sys_permission
WHERE permission_module_id = #{permissionModuleId}
ORDER BY seq ASC
LIMIT #{page.offset}, #{page.pageSize}
